application/json.schema:
{
	"$schema": "http://json-schema.org/schema",
	"type": "object",
	"description": "Book model",
	"properties": {
		"id": {
			"type": "String",
			"description": "Book id"
		},
		"titleText": {
			"type": "String",
			"description": "Book title"
		},
		"publisherName": {
			"type": "String",
			"description": "Publisher name"
		},
		"publicationDate": {
			"type": "String",
			"description": "ISO Date string"
		},
		"numberOfPages": {
			"type": "Number",
			"description": "Number of pages"
		},
		"authors": {
			"type": "Array",
			"description": "list of user names (strings)"
		},
		"topics": {
			"type": "Array",
			"description": "List of categories",
			"id": {
				"type": "String",
				"description": "Category id"
			},
			"name": {
				"type": "String",
				"description": "Category name"
			}
		},
		"languages": {
			"type": "Array",
			"description": "List of strings identifying languages"
		},
		"productIdentifier": {
			"type": "Array",
			"description": "List of product identifiers",
			"ProductIDType": {
				"type": "String",
				"description": "Type of ID"
			},
			"IDValue": {
				"type": "String",
				"description": "ID value"
			}
		},
		"productFormDetail": {
			"type": "Array",
			"description": "List of product types"
		},
		"epubTechnicalProtection": {
			"type": "Array",
			"description": "List of strings identifying technical protections"
		},
		"productSize": {
			"type": "Number",
			"description": "Product size"
		},
		"coverImageUrl": {
			"type": "String"
		},
		"downloadUrl": {
			"type": "String"
		},
		"descriptionText": {
			"type": "String"
		},
		"owned": {
			"type": "Boolean"
		}
	},
	"required": ["id", "titleText", "publisherName", "publicationDate", "numberOfPages", "authors", "topics", "languages", "productIdentifier", "productFormDetail", "epubTechnicalProtection", "productSize", "coverImageUrl", "downloadUrl", "descriptionText", "owned"]
}

